@@ -0,0 +1,136 @@
+@import "tailwindcss/preflight";
+@import "tailwindcss/utilities";
+
+@layer utilities {
+  .grid-cols-16 {
+    grid-template-columns: repeat(16, minmax(0, 1fr));
+  }
+
+  .grid-cols-30 {
+    grid-template-columns: repeat(30, minmax(0, 1fr));
+  }
+}
+
+/* Ensure grid cells are always square */
+.grid > * {
+  aspect-ratio: 1;
+  min-width: 0;
+  min-height: 0;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+}
+
+/* Ensure grid container doesn't overflow on small screens */
+.grid {
+  max-width: 100vw;
+  max-height: 80vh;
+}
+
+/* Game board container styling */
+.game-board {
+  margin: 0 auto;
+  display: flex;
+  justify-content: center;
+  align-items: center;
+}
+
+/* Handle different grid sizes */
+.grid-cols-9 {
+  grid-template-columns: repeat(9, minmax(0, 1fr));
+}
+
+.grid-cols-16 {
+  grid-template-columns: repeat(16, minmax(0, 1fr));
+}
+
+.grid-cols-30 {
+  grid-template-columns: repeat(30, minmax(0, 1fr));
+}
+
+/* Add subtle grid pattern and visual enhancements */
+.grid {
+  background-image:
+    linear-gradient(rgba(75, 85, 99, 0.1) 1px, transparent 1px),
+    linear-gradient(90deg, rgba(75, 85, 99, 0.1) 1px, transparent 1px);
+  background-size: 100% 100%;
+  box-shadow: inset 0 0 20px rgba(0, 0, 0, 0.1);
+  position: relative;
+}
+
+/* Add a subtle glow effect to the grid */
+.grid::before {
+  content: '';
+  position: absolute;
+  inset: 0;
+  background: radial-gradient(circle at 50% 50%, rgba(59, 130, 246, 0.1) 0%, transparent 70%);
+  pointer-events: none;
+  border-radius: inherit;
+}
+
+/* Custom scrollbar for the game board */
+.game-board::-webkit-scrollbar {
+  width: 8px;
+  height: 8px;
+}
+
+.game-board::-webkit-scrollbar-track {
+  background: #374151;
+  border-radius: 4px;
+}
+
+.game-board::-webkit-scrollbar-thumb {
+  background: #6b7280;
+  border-radius: 4px;
+}
+
+.game-board::-webkit-scrollbar-thumb:hover {
+  background: #9ca3af;
+}
+
+/* Responsive grid sizing */
+@media (max-width: 640px) {
+  .grid {
+    gap: 0.25rem;
+    padding: 1rem;
+  }
+
+  .game-board {
+    margin: 0 0.5rem;
+  }
+
+  /* Ensure cells are properly sized on mobile */
+  .grid > * {
+    width: 2.5rem;
+    height: 2.5rem;
+    font-size: 0.875rem;
+  }
+}
+
+@media (min-width: 641px) and (max-width: 1024px) {
+  .grid {
+    gap: 0.5rem;
+    padding: 1.5rem;
+  }
+
+  /* Medium screen cell sizing */
+  .grid > * {
+    width: 3rem;
+    height: 3rem;
+    font-size: 1rem;
+  }
+}
+
+@media (min-width: 1025px) {
+  .grid {
+    gap: 0.5rem;
+    padding: 1.5rem;
+  }
+
+  /* Large screen cell sizing */
+  .grid > * {
+    width: 3.5rem;
+    height: 3.5rem;
+    font-size: 1.125rem;
+  }
+}

