'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { GameBoard } from './GameBoard';
import { GameControls } from './GameControls';
import { GameStats } from './GameStats';

export interface Cell {
    id: string;
    row: number;
    col: number;
    isMine: boolean;
    isRevealed: boolean;
    isFlagged: boolean;
    neighborMines: number;
}

export interface GameState {
    board: Cell[][];
    gameOver: boolean;
    gameWon: boolean;
    mineCount: number;
    flaggedCount: number;
    revealedCount: number;
    startTime: number | null;
    endTime: number | null;
    difficulty: 'beginner' | 'intermediate' | 'expert';
}

const DIFFICULTY_CONFIGS = {
    beginner: { rows: 9, cols: 9, mines: 10 },
    intermediate: { rows: 16, cols: 16, mines: 40 },
    expert: { rows: 16, cols: 30, mines: 99 }
};

// Helper functions moved outside component
const createBoard = (rows: number, cols: number, mines: number): Cell[][] => {
    const board: Cell[][] = [];

    // Initialize empty board
    for (let row = 0; row < rows; row++) {
        board[row] = [];
        for (let col = 0; col < cols; col++) {
            board[row][col] = {
                id: `${row}-${col}`,
                row,
                col,
                isMine: false,
                isRevealed: false,
                isFlagged: false,
                neighborMines: 0
            };
        }
    }

    // Place mines randomly
    let minesPlaced = 0;
    while (minesPlaced < mines) {
        const row = Math.floor(Math.random() * rows);
        const col = Math.floor(Math.random() * cols);

        if (!board[row][col].isMine) {
            board[row][col].isMine = true;
            minesPlaced++;
        }
    }

    // Calculate neighbor mines for each cell
    for (let row = 0; row < rows; row++) {
        for (let col = 0; col < cols; col++) {
            if (!board[row][col].isMine) {
                board[row][col].neighborMines = countNeighborMines(board, row, col);
            }
        }
    }

    return board;
};

const countNeighborMines = (board: Cell[][], row: number, col: number): number => {
    let count = 0;
    for (let r = Math.max(0, row - 1); r <= Math.min(board.length - 1, row + 1); r++) {
        for (let c = Math.max(0, col - 1); c <= Math.min(board[0].length - 1, col + 1); c++) {
            if (board[r][c].isMine) count++;
        }
    }
    return count;
};

const initializeGame = (difficulty: 'beginner' | 'intermediate' | 'expert'): GameState => {
    const config = DIFFICULTY_CONFIGS[difficulty];
    const board = createBoard(config.rows, config.cols, config.mines);

    return {
        board,
        gameOver: false,
        gameWon: false,
        mineCount: config.mines,
        flaggedCount: 0,
        revealedCount: 0,
        startTime: null,
        endTime: null,
        difficulty
    };
};

export const MinesweeperGame: React.FC = () => {
    const [gameState, setGameState] = useState<GameState>(() => initializeGame('beginner'));

    const handleCellClick = useCallback((row: number, col: number) => {
        if (gameState.gameOver || gameState.gameWon) return;

        setGameState(prev => {
            const newBoard = [...prev.board];
            const cell = newBoard[row][col];

            if (cell.isFlagged || cell.isRevealed) return prev;

            // Start timer on first click
            if (prev.startTime === null) {
                return {
                    ...prev,
                    startTime: Date.now()
                };
            }

            if (cell.isMine) {
                // Game over - reveal all mines
                revealAllMines(newBoard);
                return {
                    ...prev,
                    board: newBoard,
                    gameOver: true,
                    endTime: Date.now()
                };
            }

            // Reveal cell and neighbors if no adjacent mines
            const newRevealedCount = revealCellAndNeighbors(newBoard, row, col);

            // Check if game is won
            const totalCells = newBoard.length * newBoard[0].length;
            const gameWon = newRevealedCount === totalCells - prev.mineCount;

            return {
                ...prev,
                board: newBoard,
                revealedCount: newRevealedCount,
                gameWon,
                endTime: gameWon ? Date.now() : null
            };
        });
    }, [gameState.gameOver, gameState.gameWon, gameState.startTime]);

    const handleRightClick = useCallback((row: number, col: number) => {
        if (gameState.gameOver || gameState.gameWon) return;

        setGameState(prev => {
            const newBoard = [...prev.board];
            const cell = newBoard[row][col];

            if (cell.isRevealed) return prev;

            const newFlaggedCount = prev.flaggedCount + (cell.isFlagged ? -1 : 1);
            newBoard[row][col] = { ...cell, isFlagged: !cell.isFlagged };

            return {
                ...prev,
                board: newBoard,
                flaggedCount: newFlaggedCount
            };
        });
    }, [gameState.gameOver, gameState.gameWon]);

    const revealAllMines = (board: Cell[][]) => {
        for (let row = 0; row < board.length; row++) {
            for (let col = 0; col < board[0].length; col++) {
                if (board[row][col].isMine) {
                    board[row][col].isRevealed = true;
                }
            }
        }
    };

    const revealCellAndNeighbors = (board: Cell[][], row: number, col: number): number => {
        const cell = board[row][col];
        if (cell.isRevealed || cell.isFlagged) return 0;

        let revealedCount = 0;
        const queue = [{ row, col }];
        const visited = new Set<string>();

        while (queue.length > 0) {
            const { row: r, col: c } = queue.shift()!;
            const currentCell = board[r][c];
            const cellKey = `${r}-${c}`;

            if (currentCell.isRevealed || currentCell.isFlagged || visited.has(cellKey)) continue;

            visited.add(cellKey);
            currentCell.isRevealed = true;
            revealedCount++;

            // If this cell has no adjacent mines, reveal all its neighbors
            if (currentCell.neighborMines === 0) {
                // Check all 8 adjacent cells (including diagonals)
                for (let nr = Math.max(0, r - 1); nr <= Math.min(board.length - 1, r + 1); nr++) {
                    for (let nc = Math.max(0, c - 1); nc <= Math.min(board[0].length - 1, c + 1); nc++) {
                        const neighborKey = `${nr}-${nc}`;
                        const neighbor = board[nr][nc];

                        // Only add unvisited, unrevealed, unflagged neighbors to the queue
                        if (!neighbor.isRevealed && !neighbor.isFlagged && !visited.has(neighborKey)) {
                            queue.push({ row: nr, col: nc });
                        }
                    }
                }
            }
        }

        return revealedCount;
    };

    const handleNewGame = (difficulty: 'beginner' | 'intermediate' | 'expert') => {
        setGameState(initializeGame(difficulty));
    };

    const handleResetGame = () => {
        setGameState(initializeGame(gameState.difficulty));
    };

    return (
        <div className="bg-white/10 backdrop-blur-sm rounded-2xl p-6 shadow-2xl border border-white/20">
            <GameControls
                onNewGame={handleNewGame}
                onResetGame={handleResetGame}
                currentDifficulty={gameState.difficulty}
            />

            <GameStats
                mineCount={gameState.mineCount}
                flaggedCount={gameState.flaggedCount}
                startTime={gameState.startTime}
                endTime={gameState.endTime}
                gameOver={gameState.gameOver}
                gameWon={gameState.gameWon}
            />

            <GameBoard
                board={gameState.board}
                onCellClick={handleCellClick}
                onRightClick={handleRightClick}
                gameOver={gameState.gameOver}
                gameWon={gameState.gameWon}
            />

            {(gameState.gameOver || gameState.gameWon) && (
                <div className="mt-6 text-center">
                    <div className={`text-2xl font-bold mb-4 ${gameState.gameWon ? 'text-green-400' : 'text-red-400'
                        }`}>
                        {gameState.gameWon ? 'ðŸŽ‰ You Won! ðŸŽ‰' : 'ðŸ’¥ Game Over! ðŸ’¥'}
                    </div>
                    <button
                        onClick={handleResetGame}
                        className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-6 rounded-lg transition-colors duration-200"
                    >
                        Play Again
                    </button>
                </div>
            )}
        </div>
    );
};
