+++ 
@@ -0,0 +1,87 @@
+'use client';
+
+import React, { useState } from 'react';
+
+interface WindowsMenuProps {
+    onNewGame: (difficulty: 'beginner' | 'intermediate' | 'expert') => void;
+    currentDifficulty: 'beginner' | 'intermediate' | 'expert';
+}
+
+export const WindowsMenu: React.FC<WindowsMenuProps> = ({
+    onNewGame,
+    currentDifficulty
+}) => {
+    const [isOpen, setIsOpen] = useState(false);
+
+    const difficulties = [
+        { key: 'beginner', label: 'Beginner' },
+        { key: 'intermediate', label: 'Intermediate' },
+        { key: 'expert', label: 'Expert' }
+    ] as const;
+
+    const handleDifficultySelect = (difficulty: 'beginner' | 'intermediate' | 'expert') => {
+        onNewGame(difficulty);
+        setIsOpen(false);
+    };
+
+    return (
+        <div className="relative">
+            {/* Menu Bar Button */}
+            <button
+                onClick={() => setIsOpen(!isOpen)}
+                className="bg-gray-300 text-black px-3 py-1 text-sm font-bold"
+                style={{
+                    border: '2px outset #c0c0c0',
+                    fontFamily: 'MS Sans Serif, sans-serif'
+                }}
+                onMouseDown={(e) => {
+                    e.currentTarget.style.border = '2px inset #c0c0c0';
+                }}
+                onMouseUp={(e) => {
+                    e.currentTarget.style.border = '2px outset #c0c0c0';
+                }}
+                onMouseLeave={(e) => {
+                    e.currentTarget.style.border = '2px outset #c0c0c0';
+                }}
+            >
+                Game
+            </button>
+
+            {/* Dropdown Menu */}
+            {isOpen && (
+                <div
+                    className="absolute top-full left-0 z-50 bg-gray-300 border-2 border-black"
+                    style={{
+                        fontFamily: 'MS Sans Serif, sans-serif',
+                        minWidth: '120px'
+                    }}
+                >
+                    {difficulties.map((difficulty) => (
+                        <button
+                            key={difficulty.key}
+                            onClick={() => handleDifficultySelect(difficulty.key)}
+                            className={`w-full text-left px-3 py-1 text-sm font-bold ${currentDifficulty === difficulty.key
+                                    ? 'bg-blue-600 text-white'
+                                    : 'bg-gray-300 text-black hover:bg-blue-600 hover:text-white'
+                                }`}
+                            style={{
+                                border: 'none',
+                                outline: 'none'
+                            }}
+                        >
+                            {difficulty.label}
+                        </button>
+                    ))}
+                </div>
+            )}
+
+            {/* Click outside to close */}
+            {isOpen && (
+                <div
+                    className="fixed inset-0 z-40"
+                    onClick={() => setIsOpen(false)}
+                />
+            )}
+        </div>
+    );
+};

