'use client';

import React from 'react';

interface WindowsWindowProps {
    title: string;
    children: React.ReactNode;
    menuBar?: React.ReactNode;
}

export const WindowsWindow: React.FC<WindowsWindowProps> = ({
    title,
    children,
    menuBar
}) => {
    return (
        <div className="inline-block" style={{
            fontFamily: 'MS Sans Serif, sans-serif'
        }}>
            {/* Title Bar - Blue background with window controls */}
            <div className="bg-blue-600 text-white px-2 py-1 flex justify-between items-center" style={{
                border: '2px outset #c0c0c0',
                minHeight: '20px'
            }}>
                {/* App Title */}
                <div className="text-sm font-bold">
                    {title}
                </div>

                {/* Window Controls */}
                <div className="flex gap-1">
                    {/* Minimize Button */}
                    <button className="w-4 h-4 bg-gray-300 border-2 border-outset" style={{
                        border: '2px outset #c0c0c0'
                    }}>
                        <div className="w-2 h-0.5 bg-black mx-auto mt-1.5"></div>
                    </button>

                    {/* Maximize Button */}
                    <button className="w-4 h-4 bg-gray-300 border-2 border-outset" style={{
                        border: '2px outset #c0c0c0'
                    }}>
                        <div className="w-2 h-2 bg-black mx-auto mt-0.5"></div>
                    </button>

                    {/* Close Button */}
                    <button className="w-4 h-4 bg-gray-300 border-2 border-outset" style={{
                        border: '2px outset #c0c0c0'
                    }}>
                        <div className="w-2 h-0.5 bg-black mx-auto mt-1.5 transform rotate-45"></div>
                        <div className="w-2 h-0.5 bg-black mx-auto mt-0.5 transform -rotate-45"></div>
                    </button>
                </div>
            </div>

            {/* Menu Bar */}
            {menuBar && (
                <div className="bg-gray-300 border-b-2 border-black px-2 py-1">
                    {menuBar}
                </div>
            )}

            {/* Window Content */}
            <div className="bg-gray-300 p-3" style={{
                border: '3px outset #c0c0c0'
            }}>
                {children}
            </div>
        </div>
    );
};
